const axios = require("./index");
const assert = require("assert");

const imageAsBase64 =
	"data:image/png;base64,";
const buffer = Buffer.from("Hello World");

const assertEq = (res, testVal) => assert.equal(res, testVal);
const assertDeepEq = (res, testVal) => assert.deepEqual(res, testVal);
const breakWithErr = err => {
	console.error(err);
	process.exit(1);
};

/**
 * GET
 */
axios("http://httpbin.org/get")
	.then(response => assertEq(response.status, 200))
	.catch(err => breakWithErr(err));

const getWithParams = { r: "1", hello: "World" };
axios("http://httpbin.org/get?hello=World&r=1")
	.then(response => assertDeepEq(response.body.args, getWithParams))
	.catch(err => breakWithErr(err));

/**
 * POST
 */
const postFormData = { hello: "world" };
axios("http://httpbin.org/post", {
	method: "POST",
	body: {
		hello: "world"
	}
})
	.then(response => assertDeepEq(response.body.form, postFormData))
	.catch(err => breakWithErr(err));

const postJSON = { hello: "world" };
axios("http://httpbin.org/post", {
	method: "POST",
	body: {
		hello: "world"
	},
	headers: {
		"content-type": "application/json"
	}
})
	.then(response => assertDeepEq(response.body.json, postJSON))
	.catch(err => breakWithErr(err));

const postBufferData = "Hello World";
axios("http://httpbin.org/post", {
	method: "POST",
	body: buffer
})
	.then(response => assertDeepEq(response.body.data, postBufferData))
	.catch(err => breakWithErr(err));

axios("http://httpbin.org/post", {
	method: "POST",
	body: {
		image: imageAsBase64
	}
})
	.then(response =>
		assertDeepEq(response.body.form, { image: imageAsBase64 })
	)
	.catch(err => breakWithErr(err));

/**
 * PUT
 */
axios("http://httpbin.org/put", {
	method: "PUT",
	body: {
		hello: "world"
	},
	headers: {
		"content-type": "application/json"
	}
})
	.then(response => assertDeepEq(response.body.json, postJSON))
	.catch(err => breakWithErr(err));

/**
 * DELETE
 */
axios("http://httpbin.org/delete", {
	method: "DELETE"
})
	.then(response => assertEq(response.status, 200))
	.catch(err => breakWithErr(err));

/**
 * HEAD
 */
axios("http://httpbin.org/headers", {
	method: "HEAD"
})
	.then(response => assertEq(response.status, 200))
	.catch(err => breakWithErr(err));

/**
 * REDIRECT
 */
axios("http://httpbin.org/redirect-to?url=http%3A%2F%2Fhttpbin.org%2Fget")
	.then(response => assertEq(response.status, 200))
	.catch(err => breakWithErr(err));

axios("http://httpbin.org/redirect-to?url=http%3A%2F%2Fhttpbin.org%2Fget", {
	followredirect: false
})
	.then(response => assertEq(response.status, 302))
	.catch(err => breakWithErr(err));

/**
 * HTTPS
 */
axios("https://httpbin.org/get")
	.then(response => assertEq(response.status, 200))
	.catch(err => breakWithErr(err));

/**
 * ASYNC/AWAIT
 */
(async () => {
	const response = await axios("https://httpbin.org/get");
	assertEq(response.status, 200);
})();

/**
 * Basic Auth
 */
axios("https://httpbin.org/basic-auth/user/passwd", {
	auth: "user:passwd"
})
	.then(response => assertEq(response.status, 200))
	.catch(err => breakWithErr(err));
